                                            ->leftJoin('n.CsComments nc')
                                            ->leftJoin('nc.CsUsers ncu')
                                            ->andWhere('n.created_at >= ?', array( $startdate ))
                                            ->andWhere('n.created_at <= ?', array( $enddate ))
                                            #->setHydrationMode(Doctrine::HYDRATE_ARRAY)
