    /**
     * fetchLatestNews
     *
     * Doctrine_Query to fetch Latest News
     */
    public static function fetchLatestNews($numberNews)
    {
        # fetch news via doctrine query
        $latestnews = Doctrine_Query::create()
                              ->select('n.*, u.nick, u.user_id, c.name, c.image')
                              ->from('CsNews n')
                              ->leftJoin('n.CsUsers u')
                              ->leftJoin('n.CsCategories c')
                              ->setHydrationMode(Doctrine::HYDRATE_ARRAY)
                              ->orderby('n.news_id DESC')
                              ->limit($numberNews)
                              ->execute( array() );

        # put things in an array-box for delivery multiple things with one return stmt
        return $latestnews;
    }

    /**
     * fetch used News Categories
     *
     * Doctrine_Query to fetch all used News Categories
     */
    public static function fetchUsedNewsCategories()
    {
        # fetch news via doctrine query
        $newscategories = Doctrine_Query::create()
                                    ->select('n.cat_id, COUNT(n.cat_id) sum_news, c.name')
                                    ->from('CsNews n')
                                    ->leftJoin('n.CsCategories c ON n.cat_id = c.cat_id')
                                    ->where('c.module_id = 7')
                                    ->groupBy('c.name')
                                    ->setHydrationMode(Doctrine::HYDRATE_ARRAY)
                                    ->execute( array() );

        return $newscategories;
    }
    
    /**
     * fetch all News Categories
     *
     * Doctrine_Query to fetch all News Categories for display with an 
     * 
     * @todo check if there is an hydation mode for a relationship of two var (key => value) 
     */
    public static function fetchAllNewsCategoriesDropDown()
    {
        # fetch news via doctrine query
        $newscategories = Doctrine_Query::create()
                                    ->select('c.cat_id, c.name')
                                    ->from('CsCategories c')
                                    ->where('c.module_id = 7')
                                    ->groupBy('c.name')
                                    ->setHydrationMode(Doctrine::HYDRATE_ARRAY)
                                    ->execute( array() );        
        
        /**
         * restructure numbered array to 'cat_id' => 'name' for the dropdown
         */
        $newscategories_dropdown = array();
        
        foreach($newscategories as $array_element)
        {
            $newscategories_dropdown[$array_element['cat_id']] =  $array_element['name'];
        }
        
        return $newscategories_dropdown;
    }

    public static function fetchNewsArchiveWidget()
    {
        # fetch all newsentries, ordered by creation date ASCENDING
        $widget_archive = Doctrine_Query::create()
                                    ->select('n.news_id, n.created_at')
                                    ->from('CsNews n')
                                    ->setHydrationMode(Doctrine::HYDRATE_ARRAY)
                                    ->orderby('n.created_at ASC')
                                    ->execute( array() );

        return $widget_archive;
    }
